<?phpnamespace Guava\FilamentIconPicker\Forms\Concerns;trait HasIconValidation {    public function validateIconName(?string $icon): ?string {        if (!$icon) {            return null;        }        $icon = str_replace(' ', '-', $icon);        $base = base_path('vendor/owenvoke/blade-fontawesome/resources/svg');        if (str_starts_with($icon, 'fas-')) {            $path = $base . '/solid/' . str_replace('fas-', '', $icon) . '.svg';        } elseif (str_starts_with($icon, 'fab-')) {            $path = $base . '/brands/' . str_replace('fab-', '', $icon) . '.svg';        } elseif (str_starts_with($icon, 'far-')) {            $path = $base . '/regular/' . str_replace('far-', '', $icon) . '.svg';        } else {            return null;        }        return file_exists($path) ? $icon : null;    }}